import { Component, OnDestroy } from '@angular/core';
import { Router } from '@angular/router';

import { Subscription } from 'rxjs';

import { TreeNode } from 'primeng/api';
import { MessageService as PrimeMessageService } from 'primeng/api';

import { GlobalMessageService } from './services/global-message.service';
import { GlobalMessageType } from './interfaces/global-message-type';

import { Navigation } from './navigation';


interface City {
  name: string,
  code: string,
  disabled?: boolean
}
@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss'],
  providers: [ GlobalMessageService, PrimeMessageService ]
})

export class AppComponent implements OnDestroy {
  title = 'developer';
  navigation: any;
  messages: any = [];
  subscription: Subscription;


  cities: City[];
  selectedCity: City;


  sourceCode: string = "";

  constructor(
    private router: Router,
    public primeMessageService: PrimeMessageService,
    private globalMessageService: GlobalMessageService
  ) {
    this.selectedCity = {
      name: '',
      code: ''
    }
    this.cities = [
      {name: 'New York', code: 'NY'},
      {name: 'Rome, Italy', code: 'RM'},
      {name: 'London', code: 'LDN'},
      {name: 'Istanbul', code: 'IST'},
      {name: 'Paris', code: 'PRS'}
    ];


    this.navigation = Navigation;
    this.subscription = this.globalMessageService.getMessage().subscribe((message: GlobalMessageType) => {
      if (message.key === 'cux-banner') {
        this.messages.push(message);
        this.primeMessageService.add(message);
      }
    });
  }

  nodeSelect(event) {
    if (event.node.type === 'navigation' && event.node.data) {
      this.router.navigateByUrl(event.node.data);
    }
  }

  expandAll(){
    this.navigation.forEach(node => {
      this.recursiveToggle(node, true);
    });
  }

  collapseAll(){
    this.navigation.forEach( node => {
      this.recursiveToggle(node, false);
    });
  }

  recursiveToggle(node:TreeNode, isExpand:boolean){
    node.expanded = isExpand;
    if (node.children) {
      node.children.forEach( childNode => {
        this.recursiveToggle(childNode, isExpand);
      });
    }
  }

  ngOnDestroy() {
    this.subscription.unsubscribe();
  }

}

export interface GlobalMessageType {
  severity?: string;
  summary?: string;
  detail?: string;
  id?: any;
  key?: string;
  life?: number;
  sticky?: boolean;
  closable?: boolean;
  data?: any;
  icon?: string;
  contentStyleClass?: string;
  styleClass?: string;
}

export interface GuxButtonType {
  /**
  * When present, it specifies the type of the button
  */
  type?: 'button' | 'submit' | 'reset';
  /**
  * When present, it specifies the text of the button
  */
  label?: string;
  /**
  * Style of the button color
  */
  buttonMode?: 'primary' | 'secondary' | 'success' | 'info' | 'warning' | 'help' | 'danger';
  /**
  * Size of the button
  */
  buttonSize?: 'sm' | 'lg';
  /**
  * When present, it specifies the icon to be displayed in the button
  */
  icon?: string;
  /**
  * Position of the icon displayed in the button
  */
  iconPosition?: 'left' | 'right';
  /**
  * When present, it specifies the tooltip text for the button
  */
  tooltip?: string;
  /**
  * Position of the tooltip displayed for the button
  */
  tooltipPosition?: 'top' | 'left' | 'bottom' | 'right';
  /**
  * When present and true, it specifies that the button should be disabled
  */
  isDisabled?: boolean;
  /**
  * When present and true, it specifies that the button should be text only
  */
  isTextOnly?: boolean;
  /**
  * When present and true, it specifies that the button should be raised
  */
  isRaised?: boolean;
  /**
  * When present and true, it specifies that the button should be rounded
  */
  isRounded?: boolean;
  /**
  * When present and true, it specifies that the button should be outlined
  */
  isOutlined?: boolean;
  /**
  * When present, it specifies that the inline style to apply to the button
  */
  inlineStyle?: string;
}

export interface GuxDropdownOptionType {
  /**
  * Label for this option
  */
  label: string;
  /**
  * Value for this option
  */
  value: string;
  /**
  * When present, it specifies that this option should be disabled
  */
  disabled?: boolean;
}

import { GuxDropdownOptionType } from "./gux-dropdown-option.interface";

export interface GuxDropdownType {
  /**
  * Items to be displayed in the dropdown
  */
  options: GuxDropdownOptionType[];
  /**
  * Name of the label field of an option
  */
  optionLabel?: string;
  /**
  * Name of the value field of an option
  */
  optionValue?: string;
  /**
  * Name of the disabled field of an option
  */
  optionDisabled?: string;
  /**
  * Name of the input element
  */
  name?: string;
  /**
  * Value of the label to be displayed
  */
  label?: string;
  /**
  * Position of the label, valid values are top, left and float
  */
  labelPosition?: string;
  /**
  * When present, it specifies that an input field must be filled out before submitting the form
  */
  required?: boolean;
  /**
  * Error information to display if the dropdown is required and none selected
  */
  requiredError?: string;
  /**
  * When present, it specifies that the component should be disabled
  */
  isDisabled?: boolean;
  /**
  * Default text to display when no option is selected
  */
  placeholder?: string;
  /**
  * Icon class of the dropdown icon
  */
  dropdownIcon?: string;
  /**
  * When enabled, a clear icon is displayed to clear the value
  */
  showClear?: boolean;
  /**
  * Height of the viewport in pixels, a scrollbar is defined if height of list exceeds this value
  */
  scrollHeight?: string;
  /**
  * Advisory information to display in a tooltip on hover
  */
  tooltip?: string;
  /**
  * Position of the tooltip, valid values are right, left, top and bottom
  */
  tooltipPosition?: string;
}

